const joinChannel = serverVars("createVoice");
if (joinChannel) {
    const newUserChannel = tempVars("after")
        .voiceChannel;
    const oldUserChannel = tempVars("prior")
        .voiceChannel;
    const TheUser = tempVars("after");
    const parent_ID = joinChannel.parentID;
    const server = cache.server;
    const Vname = TheUser.displayName + "-private";

    if (newUserChannel == joinChannel) {
        if (oldUserChannel && oldUserChannel.name == Vname) {
            oldUserChannel.delete();
            server.createChannel(Vname, {
                    type: 'voice',
                    parent: parent_ID
                })
                .then(function (channel) {
                    TheUser.setVoiceChannel(channel);
                }.bind(this))
                .catch(this.displayError.bind(this));
        } else {
            server.createChannel(Vname, {
                    type: 'voice',
                    parent: parent_ID
                })
                .then(function (channel) {
                    TheUser.setVoiceChannel(channel);
                }.bind(this))
                .catch(this.displayError.bind(this));
        }
    } else if (oldUserChannel && oldUserChannel !== joinChannel && oldUserChannel.members.size == 0 && oldUserChannel.name == Vname) {
        oldUserChannel.delete();
    } else if (oldUserChannel && oldUserChannel !== joinChannel && oldUserChannel.members.size == 0 && oldUserChannel.name.includes('-private')) {
        oldUserChannel.delete();
    }
}
